settings.custom_liquid:
{% assign all_tags = '' | split: ',' %}

{% for product in collections.all.products %}
  {% assign all_tags = all_tags | concat: product.tags %}
{% endfor %}

{% assign unique_tags = all_tags | uniq %}
{% assign limited_tags = unique_tags | slice: 0, 25 %}

<div class="tag-list">
  <h3>Explore Popular searches</h3>
  <ul>
    {% for tag in limited_tags %}
      <li><a href="/collections/all/{{ tag | handle }}">{{ tag }}</a></li>
    {% endfor %}
  </ul>
</div>
